-- ----------------------------
-- Table structure for policy
-- ----------------------------
DROP TABLE IF EXISTS `policy`;
CREATE TABLE `policy`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '标题',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '内容',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '发布时间',
  `type` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '政策类型',
  `num` int NULL DEFAULT 0 COMMENT '点赞量',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '扶贫政策表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of policy
-- ----------------------------
INSERT INTO `policy` VALUES (1, '助农政策：支持农业发展，提高农民收入', '<p>助农政策是指政府为了支持农业发展，提高农民收入，采取的一系列政策和措施。这些政策包括财政、税收、金融、保险、科技、教育、文化、卫生等各个方面，旨在帮助农民增加收入，改善生活条件，提高农业生产效率，促进农村经济发展。</p><p>以下是一些常见的助农政策：</p><ol><li>财政支持：政府通过财政手段，对农业进行直接补贴、专项资金支持等，以增加农民收入和促进农业发展。</li><li>税收优惠：政府对从事农业生产和相关活动的企业和个人给予税收优惠，以降低其经营成本，提高其盈利能力。</li><li>金融支持：政府通过建立农业银行、农村信用社等金融机构，为农民提供贷款、担保等金融服务，以支持农业生产和农村经济发展。</li><li>保险保障：政府通过建立农业保险制度，为农民提供风险保障，以降低自然灾害等对农业生产的影响。</li><li>科技支持：政府通过推广先进的农业技术、设备和工艺，提高农业生产效率和质量，以增加农民收入和促进农业现代化。</li><li>教育培训：政府通过开展农民教育培训，提高农民的文化素质和技能水平，以促进农村劳动力的转移和增加农民收入。</li><li>文化传承：政府通过开展农村文化活动，传承和弘扬农村优秀传统文化，以丰富农民的精神文化生活和促进农村文化发展。</li><li>卫生健康：政府通过加强农村卫生体系建设，提高农民的健康水平和生活质量，以促进农村社会的和谐稳定和发展。</li></ol><p>总之，助农政策是政府为了支持农业和农村发展而采取的一系列政策和措施，旨在提高农民收入和生活水平，促进农业和农村的可持续发展。</p>', '2023-12-19', '金融扶贫', 2);
INSERT INTO `policy` VALUES (2, '社会救助扶贫政策', '<p>1、城乡最低生活保障。</p><p>保障标准:2018年市区城乡低保标准分别为城市月人均540元和农村年人均3600元。</p><p>申请条件:户籍状况、家庭收入和家庭财产是认定低保对象的三个基本条件。凡持有当地常住户口，共同生活的家庭成员人均可支配收入低于当地低保标准，且家庭财产状况符合当地人民政府规定条件的家庭，可以申请低保。</p><p>保障措施:对家庭主要劳动力因病、残、灾和年龄等因素导致贫困的农村家庭，以及身体残疾、孤寡老人、长期患病等通过产业扶持和就业帮助无法实现脱贫的农村贫困家庭，除纳入孤儿保障、特困人员救助供养范围的，其余均应按政策规定纳入低保保障范围，实行低保兜底。</p><p>对因病支出型贫困家庭，家庭年收入扣减个人自负医疗费用后，人均额度低于当地低保标准，且家庭财产情况符合当地申请最低生活保障家庭经济状况认定条件的“支出型”贫困家庭，纳入低保保障范围，享受一年最低生活保障待遇。</p><p>对农村低保家庭中的老年人、未成年人、重度残疾人、重病患者、一户多残人员、老残一体人员以及当地县级民政部门认定的其他特殊困难人员给予重点保障，增发补助金，平均增发比例不低于当地低保标准的50%。</p><p>对于低保家庭中的急性白血病(再生障碍性贫血)、恶性肿瘤、严重传染性肝炎(并发症)、系统性红斑狼疮晚期、肇事肇祸重性精神病、急性传染性肺结核、国家规定的特种传染病、严重心脑血管疾病、严重肾病综合症慢性肾衰竭(尿毒症)、急性心率衰竭、心肌梗塞、风湿性心脏病及当地县级民政部门确定的其他重特大疾病患者且有持续重大支出的，按不低于当地低保标准60%比例增发补助金。</p><p>对于低保家庭中的70周岁以上老年人、 二级以上视力(智力、肢体、精神)残疾人员、老残一体人员、一户多残人员以及多重残疾人员， 按不低于当地低保标准 50%比例增发补助金。</p><p>对享受扶贫政策后暂时脱贫，家庭人均可支配收入低于当地低保标准2倍的建档立卡家庭，继续享受农村低保待遇到2020年底，使其通过扶贫政策和低保政策的“合力”实现稳定脱贫。低保补助金按暂时脱贫前核定水平确定，并在2020年底前逐年递减，年度递减幅度为25%。</p><p>对非建档立卡低保家庭就业或自主创业后暂时脱贫， 家庭人均可支配收入低于当地低保标准2倍的家庭，继续享受农村低保待遇到2020年底，低保补助金按暂时脱贫前核定水平确定，并在2020年底前逐年递减，年度递减幅度为25%。</p><p>2、特困人员救助供养。</p><p>供养范围:拥有白山市行政区域户籍的城乡60周岁以上老年人、残疾人以及未满16周岁的未成年人，同时具备无劳动能力、无法定赡(抚、扶)养义务人或者其法定义务人无履行义务能力条件的，可依法申请享受特困人员救助供养待遇。</p><p>供养形式:分为在家分散供养和在供养服务机构集中供养。</p><p>供养标准:我市城乡特困人员救助供养基本生活标准为城市(城市三无)月人均663元，农村(农村五保)月人均375元;照料护理标准为生活自理月人均158元，半失能月人均237元，失能月人均395元。</p><p>3、医疗救助</p><p>(1)救助范围。</p><p>①重点保障对象:第一类特困供养人员;第二类最低生活保障对象。</p><p>②一般保障对象:第三类低收入家庭的老年人、未成年人、重病患者、重度残疾人;第四类因病致贫家庭重病患者(指因医疗费用支出超过家庭负担能力，导致家庭实际生活水平低于当地低保家庭标准的，且家庭财产符合当地城乡居民申请最低生活保障家庭经济状况认定条件的家庭)。</p><p>(2)救助方式及标准。</p><p>①资助参保参合。第一类救助对象全额资助(2018年标准240元/人/年);第二类救助对象定额资助(2018年标准100元/人/年)。</p><p>②直接医疗救助。2018年大病保险起付线:城市低保10000元，农村(特困、低保、建档立卡贫困人口)3000元;封顶线按救助对象类别设定:重点救助对象2万元;一般救助对象1万元;年度累计额度不得超过封顶线。</p><p>(3)基本医疗救助。</p><p>①基本医疗住院救助。第一类救助对象:封顶线内全额补助;第二类救助对象:大病保险起付线的15%(含)以下按20%补助，15%-40%(含)按30%补助，40%-大病保险起付线(含)按40%补助。</p><p>②基本医疗门诊救助。第一类、第二类救助对象患下列疾病的，根据病种给予定额救助。甲类:血液病、恶性肿瘤、肾功能衰竭(尿毒症)、脑瘫500元/半年;乙类:糖尿病(胰岛素依赖者)、结核、重症肝炎、肺心病、精神分裂病、类风湿(活动期肌力、关节、心肌改变丧失劳动能力的)、脑出血、脑血栓后遗症(肌力一、二级的)300元/半年;丙类:冠心病(心功能三级以上的)、高血压200元/半年。</p><p>(4)重特大疾病医疗救助。</p><p>①重特大疾病住院救助。第一类救助对象封顶线内全额补助;第二类救助对象补助比例70%;第三类救助对象补助比例30%;第四类救助对象起助线为大病保险起付线的2倍，起助线以上部分按40%补助。救助对象中的14周岁以下(含)儿童，救助比例上浮10%。</p><p>②特殊疾病救助病种包括:肇事肇祸重性精神病;尿毒症;肺结核(痰培养化验为阳性);慢粒细胞白血病;不能切除或发生转移的胃肠道恶性间质瘤。补助标准:重点救助对象在政策范围内自付医疗费用封顶线内全额补助，一般救助对象参照重特大疾病住院救助补助标准执行。</p><p>③大病门诊救助。单次政策范围内自付医疗费用400元以上部分，重点救助对象给予不低于30%的补助;一般救助对象，按重特大疾病住院年度政策范围内自付医疗费用累计，执行重特大疾病住院救助标准。</p><p>4、临时救助。</p><p>救助范围:因遭遇灾害、事故等意外事件，导致基本生活暂时出现严重困难的;因突发重大疾病，基本生活陷入困境，导致基本生活暂时出现严重困难的低收入家庭;因生活必需支出突然增加超出家庭承受能力，导致基本生活暂时出现严重困难的低保家庭。</p><p>救助标准:临时救助标准(每人)=当地当期城市低保标准×相应月数。“相应月数”一般按1-3个月掌握，最多不超过6个月。</p><p>5、高龄补贴。</p><p>高龄老年人是指80周岁以上的老年人，高龄老年人生活津贴制度是一项社会福利制度。白山市参照《吉林省人民政府办公厅关于发放高龄老年人生活津贴的通知》[吉政办明电(2010)148号]规定执行，80~89周岁的城乡低保老年人，每人每月发放50元的生活津贴(由民政部门发放);90~99周岁的城乡老年人每人每月发放100元的生活津贴;100周岁以上的老年人每人每月发放300元的生活津贴，(由浑江区老龄办发放)。具备条件的地方，可在省人民政府规定的基础上，扩大发放范围，提高津贴标准，白山地区除长白外，其他县市区都执行省里统一标准，长白县执行标准为80~89周岁的城乡低保老年人，每人每月发放100元的生活津贴(由民政部门发放)，90~99周岁的城乡老年人每人每月发放200元的生活津贴;100周岁以上的老年人每人每月发放600元的生活津贴。</p>', '2023-12-19', '健康扶贫', 1);
INSERT INTO `policy` VALUES (4, '教育扶贫政策——各地因地制宜确定学前教育资助政策', '<p>1、学前教育。按照“地方先行、中央补助”的原则，各地因地制宜确定学前教育资助政策。</p><p>(1)政府资助。资助普惠性幼儿园在园家庭经济困难儿童、孤儿和残疾儿童。</p><p>(2)幼儿园资助。幼儿园从事业收入中提取一定比例的经费，用于减免收费、提供特殊困难补助等。</p><p>(3)社会资助。各地建立和完善相关优惠政策，引导和鼓励社会团体、企事业单位及个人等捐资，帮助家庭经济困难儿童、孤儿和残疾儿童接受普惠性学前教育。</p><p>2、义务教育。实施“两免一补”和营养改善计划。</p><p>(1)免学杂费。全部免除义务教育阶段所有学生学杂费</p><p>(2)免费教科书。对义务教育阶段所有学生免费提供教科书。为小学一年级新生免费提供正版学生字典。</p><p>(3)寄宿生生活补助。补助城乡家庭经济困难寄宿生生活费，小学生每生每年1000元，初中生每生每年1250元。</p><p>(4)营养改善计划。为国家试点地区农村义务教育阶段学生提供膳食补助， 标准为每生每年800元。地方因地制宜实施营养餐地方试点。</p><p>3、普通高中教育。以国家助学金、建档立卡等家庭经济困难学生免学杂费、地方政府资助项目为主，学校资助和社会力量积极参与。</p><p>(1)免学杂费。免除公办普通高中建档立卡家庭经济困难学生(含非建档立卡家庭经济困难残疾学生、农村低保家庭学生、农村特困救助供养学生)学杂费。对在政府教育行政管理部门依法批准的民办普通高中就读的符合免学杂费政策条件的学生，按照当地同类型公办普通高中免学杂费标准予以补助。</p><p>(2)国家助学金。资助普通高中家庭经济困难学生，平均资助标准为每生每年2000元。</p><p>(3)地方政府资助。部分地区出台地方性奖学金、助学金、专项免费等政策。</p><p>(4)校内资助。学校从事业收入中提取一定比例的经费,用于减免学费、设立校内奖助学金和特殊困难补助等支出 。</p><p>(5)社会资助。积极引导和鼓励企业、社会团体及个人等面向普通高中设立奖学金、助学金。</p><p>4、中等职业教育。以国家免学费、国家助学金为主，学校资助、社会资助和顶岗实习为补充。</p><p>(1)免学费。免除公办中等职业学校全日制正式学籍一、二、三年级在校生中所有农村(含县镇)学生城市涉农专业学生和家庭经济困难学生学费。对在民办中等职业学校就读的一、二、三年级符合免学费政策条件的学生，按照当地同类型同专业公办中等职业学校免除学费标准给予补助。</p><p>(2)国家助学金。资助全日制正式学籍一、二年级在校涉农专业学生和非涉农专业家庭经济困难学生，平均资助标准每生每年2000元。</p><p>(3)顶岗实习。安排中等职业学校三年级学生到企业等单位顶岗实习，获得一定报酬，用于支付学习和生活费用。</p><p>(4)校内资助。中等职业学校每年安排一定的经费，用于学费减免、勤工助学、校内奖学金和特殊困难补助等。</p><p>(5)社会资助。鼓励和支持社会团体、企事业单位以及个人资助中职学校家庭经济困难学生。</p><p>5、本专科生教育。“奖贷助勤补免+绿色通道”等多元混合资助</p><p>(1)国家奖学金。奖励5万名特别优秀的二年级以上(含二年级)本专科在校生，每生每年8000元。</p><p>(2)国家励志奖学金。奖励资助品学兼优、 家庭经济困难的二年级以上(含二年级)本专科在校生，每生每年5000元。</p><p>(3)国家助学金。资助家庭经济困难的本专科在校生，平均标准为每生每年3000元。</p><p>(4)国家助学贷款。政府主导、金融机构向高校家庭经济困难学生提供的免担保、免抵押信用贷款，用于解决在校期间的学费和住宿费，每学年不超过8000元。在校学习期间利息全部由财政贴付，毕业后的利息由借款学生负担。</p><p>(5)基层就业国家资助。对中央高校应届毕业生，自愿到中西部地区和艰苦边远地区基层单位就业、服务期达到3年以上(含3年)的，补偿学费或代偿国家助学贷款，每生每年不高于8000元。地方高校毕业生学费补偿贷款代偿由各地参照中央政策制定执行。</p><p>(6)服义务兵役国家资助。对应征入伍服义务兵役的高校学生在校期间缴纳的学费或获得的国家助学贷款，实施一次性补偿或代偿，对退役后复学的高校在校生(含高校新生)减免学费。资助标准每生每年不高于8000元。</p><p>(7)直招士官国家资助。对直接招收为士官的高等学校学生，入伍时对其在校期间缴纳的学费实行一次性补偿或对其获得的国家助学贷款实行代偿，每生每年不高于8000元。</p><p>(8)师范生免费教育。在北京师范大学、华东师范大学、东北师范大学、华中师范大学、陕西师范大学和西南大学六所教育部直属师范大学实行师范生费教育。免缴在校学习期间学费、住宿费，并补助生活费。地方师范院校师范生资助由各地自行实施。</p><p>(9)退役士兵学费资助。对退役一年以上、考入全日制普通高等学校的自主就业退役士兵，给予学费资助，每生每年不高于8000元。</p><p>(10)新生入学资助项目。对中西部地区高校家庭经济困难新生一次性发放路费及短期生活费补助，省(区、市)内</p><p>院校录取的每人500元，省(区、市)外院校录取的每人1000元。</p><p>(11)勤工助学。学校设置校内勤工助学岗位，优先向家庭经济困难学生提供。劳动报酬原则上不低于当地政府或有关部门制定的最低工资标准或居民最低生活保障标准。</p><p>(12)校内资助。学校设立校内奖学金、助学金、困难补助、伙食补贴、校内无息借款、减免学费等，利用事业收入资金以及社会团体、企事业单位和个人捐助资金实施校内资助。</p><p>(13)绿色通道。全日制普通高校建立“绿色通道”，对被录取入学、无法缴纳学费的家庭经济困难新生，先办理入学手续，然后再根据学生实际情况，分别采取不同办法予以资助。</p>', '2023-12-19', '教育扶贫', 1);
INSERT INTO `policy` VALUES (6, '激活农村市场，助推农业发展——促进农产品流通与消费', '<p>一、引言</p><p>农业是我国的基础产业，农产品的流通与消费是促进农业发展的重要环节。然而，当前我国农产品流通环节存在诸多问题，如信息不对称、流通效率低下等，限制了农业的发展。为了激活农村市场，助推农业发展，国家出台了一系列助农政策。本文将详细介绍这些政策的内容和意义。</p><p>二、政策内容</p><p>1.&nbsp;<span style=\"font-size: 14px;\">加强农产品市场建设</span></p><p>国家加强对农产品市场的建设和管理，推动农产品市场的升级改造。通过建设现代化的农产品交易中心、农贸市场等，提高农产品交易的效率和规范化程度，降低流通成本。</p><p>2.&nbsp;<span style=\"font-size: 14px;\">促进农产品流通现代化</span></p><p>国家鼓励采用现代化的物流技术和管理手段，提高农产品流通效率。通过推广冷链物流、智能物流等先进技术，保障农产品品质和安全，降低流通损耗。</p><p>3.&nbsp;<span style=\"font-size: 14px;\">扩大农产品消费市场</span></p><p>国家鼓励扩大农产品消费市场，促进农产品销售和消费。通过实施农产品地理标志保护工程、开展农超对接等活动，提高农产品品质和附加值，扩大农产品的消费群体。</p><ol><li>支持农业产业化发展</li></ol><p>国家支持农业产业化发展，推动农业产业链的完善。通过培育农业产业化龙头企业、建设农业科技园区等措施，提高农业的综合效益和竞争力，促进农业的可持续发展。</p><p>三、政策意义</p><ol><li>促进农业发展</li></ol><p>助农政策的实施有助于促进农业发展，提高农业生产效益和市场竞争力。通过加强农产品市场建设、促进农产品流通现代化等措施，为农业发展提供有力支撑。</p><ol><li>增加农民收入</li></ol><p>助农政策的实施有助于增加农民收入，提高农民的生活水平。通过扩大农产品消费市场、支持农业产业化发展等措施，为农民提供更多的销售渠道和增收机会。</p>', '2023-12-19', '产业扶贫', 3);
INSERT INTO `policy` VALUES (8, '金融扶贫政策', '<p>1、贫困地区企业上市政策。根据《中国证监会关于发挥资本市场作用服务国家脱贫攻坚战略的意见》(证监会公告〔2016〕19号)，对贫困地区企业上市提出如下政策:打赢脱贫攻坚战要采取务实有力的政策举措，以贫困地区(指国务院扶贫开发领导小组确定的国家扶贫开发工作重点县和集中连片特殊困难地区县，下同)实体经济需求为导向，以资本市场服务产业扶贫为重点，优先支持贫困地区企业利用资本市场资源，拓宽直接融资渠道，提高融资效率，降低融资成本，不断增强贫困地区自我发展能力。</p><p>(1)对注册地和主要生产经营地均在贫困地区且开展生产经营满三年、缴纳所得税满三年的企业，或者注册地在贫困地区、最近一年在贫困地区缴纳所得税不低于 2000万元且承诺上市后三年内不变更注册地的企业，申请首次公开发行股票并上市的，适用“即报即审、审过即发”政策。</p><p>(2)对注册地在贫困地区的企业申请在全国中小企业股份转让系统挂牌的，实行“专人对接、专项审核”，适用“即报即审、审过即挂”政策，减免挂牌初费。</p><p>(3)对注册地在贫困地区的企业发行公司债、资产支持证券的，实行“专人对接、专项审核”，适用“即报即审”政策。</p><p>2、扶贫小额贷款政策。根据《国务院扶贫办、财政部、人民银行、银监会、保监会关于创新发展扶贫小额信贷的指导意见》(国开办发〔2014〕78号)，对扶贫小额信贷提出了相关政策:扶贫小额信贷是为建档立卡贫困户量身定制的金融精准扶贫产品，其政策要点是“5万元以下、3年期以内、免担保免抵押、基准利率放贷、财政贴息、县建风险补偿金”。扶贫小额信贷要始终精确瞄准建档立卡贫困户，各银行业金融机构要加大对信用良好、有贷款意愿、有就业创业潜质、技能素质和一定还款能力的建档立卡贫困户支持力度。对已经脱贫的建档立卡贫困户，在脱贫攻坚期内保持扶贫小额信贷支持政策不变，力度不减。各地扶贫部门要加强对扶贫小额信贷和贴息对象的审查，在县乡村三级公告公示，防止非建档立卡贫困户“搭便车”。要将信用水平和还款能力作为发放扶贫小额信贷的主要参考标准，发放过程要符合法律法规和信贷管理规定，借款合同要明确贷款资金用途，坚持户借、户还，切实防范冒名借款、违规用款等问题。</p><p>各银行业金融机构要将扶贫小额信贷精准用于贫困户发展生产或能有效带动贫困户致富脱贫的特色优势产业，不能用于建房、理财、购置家庭用品等非生产性支出，更不能将扶贫小额信贷打包用于政府融资平台、房地产开发、基础设施建设等。各银行业金融机构在探索将扶贫小额信贷资金用于有效带动贫困户致富脱贫的特色优势产业过程中，必须坚持贫困户自愿和贫困户参与两项基本原则，使贫困户融入产业发展并长期受益，提高贫困户脱贫内生发展动力。</p><p>3、扶贫再贷款政策。根据《人民银行关于开办扶贫再贷款业务的通知》，对扶贫再贷款政策进行了规定，扶贫再贷款是人民银行为支持贫困地区地方法人金融机构发放涉农贷款提供的流动性支持。人民银行在执行稳健货币政策，加强总量调控的同时，通过扶贫再贷款有针对性地增加贫困地区地方法人金融机构资金来源，发挥扶贫再贷款的杠杆撬动作用，引导贫困地区地方法人金融机构扩大涉农贷款，为打赢脱贫攻坚战提供有力的金融支持。具体政策如下:</p><p>(1)扶贫再贷款的发放对象为《中国人民银行 财政部 银监会 证监会 保监会 扶贫办 共青团中央关于全面做好扶贫开发金融服务工作的指导意见》(银发〔2016〕65号)确定的832个贫困县和未纳入上述范围的省级扶贫开发工作重点县的农村商业银行、农村合作银行、农村信用社和村镇银行等4 类地方法人金融机构。</p><p>(2)借用的扶贫再贷款资金全部用于发放贫困地区涉农贷款，并结合当地建档立卡的相关情况，优先支持建档立卡贫困户和带动贫困户就业发展的企业、农村合作社，积极推动贫困地区发展特色产业和贫困人口创业就业，促进贫困人口脱贫致富。</p><p>(3)扶贫再贷款期限分为3 个月、6 个月和1 年三个档次。借款合同期限最长不得超过1年。单笔扶贫再贷款展期次数累计不得超过4 次，每次展期的期限不得超过借款合同期限，实际使用期限不得超过5 年。有关分支机构应结合扶贫再贷款政策效果评估情况，审批扶贫再贷款展期。</p><p>(4)扶贫再贷款实行比支农再贷款更为优惠的利率，具体按现行贫困地区支农再贷款利率执行。中国人民银行可结合货币政策调控需要和扶贫实际，适时调整扶贫再贷款利率。地方法人金融机构运用扶贫再贷款资金发放的涉农贷款利率加点幅度执行支农再贷款政策的有关规定。各分支机构要引导地方法人金融机构切实降低贫困地区涉农贷款利率水平，将扶贫再贷款优惠利率传导至贫困地区实体经济。有关分支机构应积极推动有条件的地方政府加大对扶贫贷款的财政贴息力度，以降低贫困地区融资成本。</p><p>4、中国农业银行精准扶贫信贷政策。中国农业银行精准扶贫信贷业务是指农业银行向建档立卡贫困户或对其脱贫增收起到直接促进作用的经营主体提供的各类信用业务。根据信贷资金介入方式、贷款用途、还款来源的不同，精准扶贫信贷业务主要包括以下模式:</p><p>(1)建档立卡贫困户生产经营贷款。建档立卡贫困户生产经营贷款即向建档立卡贫困户家庭内单个成员发放的，用于满足其生产经营资金需求的贷款。贷款对象包括具备劳动能力、劳动意愿且从事自主生产经营的建档立卡贫困户，以及通过吸收就业、合作经营、入股分红等方式与扶贫带动主体(农业产业化龙头企业、新型农业经营主体等)建立了利益联结机制的建档立卡贫困户。贷款额度:建档立卡贫困户生产经营贷款额度应控制在5万元以内，最高不超过10万元。还款方式:可配合财政贴息政策确定贷款结息周期。对于生产经营周期较长、经营初期还款困难的客户，可根据生产经营产生收益的周期合理设置宽限期。贷款使用方式:对于政府主导、借款农户自愿参与扶贫带动主体经营和分配的，在不违反外部监管要求的前提下，可按相关协议约定，由带动主体集中使用信贷资金。</p><p>(2)户用光伏小额扶贫贷款。户用光伏小额扶贫贷款即向建档立卡贫困户家庭内单个成员发放、用于购置分布式光伏发电设备及安装服务的贷款。贷款额度:光伏扶贫以购置单户装机容量5KW以内的分布式光伏发电设备为主，根据设备造价，5万元以内的农户小额贷款可满足借款人融资需求。借款人年龄与贷款期限:光伏发电设备运行期较长，且基本不需农户进行日常操作，因此国家将光伏扶贫工程定位为帮助年龄较大、缺乏劳动能力的建档立卡贫困户脱贫的有效途径之一。结合光伏设备运行情况和国家光伏扶贫目标，可适当延长贷款期限，且借款人申请借款时年龄与贷款期限之和可延长至最长不超过75年。</p><p>(3)易地搬迁小额扶贫贷款。易地搬迁小额扶贫贷款即向纳入政府易地扶贫搬迁方案的建档立卡贫困户及同步搬迁人口家庭内单个成员发放的、用于在安置地购房的贷款。借款人年龄:结合地方政府搬迁方案“应搬尽搬”工作要求和搬迁户整体年龄分布特点，可适当放宽借款人年龄要求。贷款额度:根据借款人年龄、健康状况和还款能力等因素合理确定贷款额度，贷款本息最高不超过贷款期限内搬迁户可获得的安置住房补贴资金总额度。贷款期限:根据用款时间和补贴资金发放时序合理约定贷款期限和还款方式，贷款期限不得超过贫困户可获得补贴资金的总期限。</p><p>(4)补缴社保小额扶贫贷款。补缴社保小额扶贫贷款即向符合一次性补缴城镇职工或城镇(乡)居民养老保险条件的建档立卡贫困户发放的用于补缴养老保险的贷款。借款人年龄与贷款期限:根据借款人领取养老金的一般年限和月还款金额占养老金收入比例合理确定贷款期限，最长不超过10年，且借款人申请借款时年龄与贷款期限之和可延长至不超过75年。宽限期:对于尚未满足领取养老金条件的借款人，可设置最长不超过3年的宽限期，宽限期内可以只偿还贷款利息。担保方式:借款人确无法提供有效担保的，可以采取信用方式办理，如有独立生活的成年子女的，应追加至少一名作为连带责任保证担保人。</p><p>(5)扶贫带动贷款。扶贫带动贷款即向对建档立卡贫困户有扶贫带动、服务作用的带动主体发放的用于生产经营的贷款。贷款对象包括通过订单农业、委托种养、吸收就业、合作经营、入股分红等方式与建档立卡贫困户建立了利益联结机制的专业大户、家庭农场、小微企业主等自然人或农民专业合作社、企事业法人等法人组织。扶贫带动贷款原则上应根据扶贫带动主体属性执行信贷制度和单项产品办法的有关规定。在扶贫带动主体纳入政府增信范围的前提下，可根据我行政府增信相关信贷政策，并结合地方政府扶贫工作要求，优化贷款额度、期限、定价等贷款要素。</p><p>(6)资产收益扶贫贷款。资产收益扶贫是指将村集体资产、农户自有资产以及各类财政扶贫资金及其形成的经营性资产资本化或股权化，相关经营主体利用这类资产产生经济效益后，贫困户按照股份或特定比例获得收益的扶贫方式。资产收益扶贫是一种特殊的扶贫带动方式。资产收益扶贫贷款即向资产收益扶贫项目经营主体(主要包括农村集体经济组织、农民专业合作社或企业法人等)发放的，用于满足设施农业、养殖、光伏、水电、矿产资源或乡村旅游等资产收益扶贫项目建设及日常经营周转资金需求的贷款。</p>', '2024-11-26', '金融扶贫', 1);
INSERT INTO `policy` VALUES (9, '水利扶贫政策', '<p>1、加快推进农村饮水安全巩固提升工程建设任务，按照农村饮水安全评价标准，统筹贫困村和非贫困村饮水安全巩固提升，全面解决贫困人口饮水安全问题，强化工程建设管理和运行管护，进一步提升农村供水保障程度。(普惠性)</p><p>2、加快推进水利支撑保障项目建设，抓好防洪抗旱减灾、农田水利工作，不断改善贫困地区水利基础条件，江河防洪能力进一步提高，农田水利灌排设施进一步配套完善。(普惠性)</p><p>3、鼓励水源开发利用、农村水电扶贫工程，支持更多贫困人口实现增收脱贫。(普惠性)</p><p>4、支持水土保持生态工程建设，使贫困地区水土流失趋势得到进一步遏制。(普惠性)</p><p>5、抓好水利劳务扶贫工作，推动水利工程建设与管护岗位吸纳建档立卡贫困家庭劳动力就业增收。(专门针对贫困户)</p>', '2024-12-03', '产业扶贫', 0);

-- ----------------------------
-- Table structure for poorapply
-- ----------------------------
DROP TABLE IF EXISTS `poorapply`;
CREATE TABLE `poorapply`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` int NULL DEFAULT NULL COMMENT '用户ID',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '申请说明',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '申请时间',
  `file` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '申请资料',
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核状态',
  `descr` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核说明',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '贫困户申请表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
