-- ----------------------------
-- Table structure for products
-- ----------------------------
DROP TABLE IF EXISTS `products`;
CREATE TABLE `products`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` double(10, 2) NOT NULL,
  `stock` int NOT NULL,
  `category` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `create_time` datetime NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 43 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of products
-- ----------------------------
INSERT INTO `products` VALUES (1, '有机大米', 90.00, 100, '粮食', '<p><strong>有机大米——自然耕作，健康之选</strong></p><p>有机大米，是在无化学肥料、农药和生长调节剂的环境中，遵循自然规律和生态学原理种植的大米。它具有以下特点：</p><ol><li><strong>纯净无污染</strong>：有机大米在生产加工过程中绝对禁止使用农药、化肥、激素等人工合成物质，并且不允许使用基因工程技术<use></use>。</li><li><strong>营养丰富</strong>：有机大米含有较多的铁质、镁质、钙质等微量元素及维生素C，而重金属及致癌的硝酸盐含量较低<use></use>。</li><li><strong>健康益处</strong>：中医认为有机大米性味甘平，有补中益气、健脾养胃的功效<use></use>。</li><li><strong>环保生态</strong>：有机大米的生产鼓励使用天然物料，减少资源浪费，对环境及生态有利<use></use>。</li></ol><p><b>价格：</b></p><p><b>90元/袋</b></p><p>选择有机大米，不仅是选择了一种健康的生活方式，也是对环境负责的选择。</p>', 'http://localhost:9090/files/1733194103722-有机大米.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (2, '农家土鸡', 39.00, 50, '畜牧', '<p><strong>自然放养，肉质鲜美 —— 农家土鸡</strong></p><p>我们的农家土鸡，放养于广阔的田野间，自由觅食，呼吸新鲜空气。它们以天然谷物和昆虫为食，不添加任何激素和抗生素，确保每一只土鸡都健康、强壮。</p><p><strong>特点：</strong></p><ol><li><strong>肉质紧实</strong>：自然放养的土鸡，肌肉发达，肉质紧实而富有弹性。</li><li><strong>营养丰富</strong>：富含优质蛋白质和必需氨基酸，是健康饮食的理想选择。</li><li><strong>味道鲜美</strong>：土鸡的肉质鲜美，汤汁浓郁，适合炖汤或红烧，是家庭聚餐的美味佳肴。</li></ol><p><b>价格：</b></p><p><b>39元/只</b></p><p>选择我们的农家土鸡，享受健康与美味的双重满足，同时支持本地农业，助力农民增收。</p>', 'http://localhost:9090/files/1733147826306-农家土鸡.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (3, '农家小红薯', 10.00, 80, '蔬菜', '<p>携手助农，共享红薯的甜蜜 —— 支持本地农业，品味自然馈赠\n\n在这片充满生机的土地上，我们的农民朋友们用勤劳的双手，种植出了一颗颗饱满的红薯。它们不仅是食物，更是农民辛勤劳动的结晶，每一颗都承载着他们对土地的热爱和对生活的希望。\n\n红薯——农民的骄傲，自然的赠礼\n\n助农增收：购买我们的红薯，您不仅能够享受到健康美味，更是在用实际行动支持本地农业，帮助农民增加收入，改善生活。\n绿色种植：我们的红薯采用传统绿色种植方式，不使用化学肥料和农药，确保每一颗红薯都是安全、健康的。\n营养价值丰富：红薯含有丰富的膳食纤维、维生素A和C，以及多种微量元素，是您健康饮食的优选。\n传统与现代的结合：我们保留传统种植方法的同时，也引入现代农业技术，提高产量和品质，让传统农产品焕发新的生命力。\n食用建议：\n\n健康烹饪：红薯适合多种烹饪方式，无论是蒸、煮、烤，都能让您享受到健康和美味。\n创意料理：尝试将红薯融入您的日常饮食，如红薯粥、红薯饼，或是红薯甜品，为您的餐桌增添色彩。</p><p><b>价格：</b></p><p><b>10.0元/kg</b></p>', 'http://localhost:9090/files/1733193799340-红薯.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (4, '清甜玉米', 19.99, 300, '蔬菜', '<p><strong>农家玉米——自然耕作，粒粒皆辛苦</strong></p><p>我们的农家玉米，种植在阳光充足、土壤肥沃的土地上，采用传统的耕作方式，不使用化学肥料和农药，确保每一颗玉米都是纯净无污染的。这些玉米凝聚了农民的辛勤汗水，是真正来自土地的绿色食品。</p><p><strong>特点：</strong></p><ol><li><strong>自然成熟</strong>：农家玉米在适宜的季节自然成熟，保证了玉米的新鲜和甜美。</li><li><strong>营养丰富</strong>：富含膳食纤维、维生素和矿物质，是健康饮食的好选择。</li><li><strong>传统种植</strong>：遵循传统农业智慧，采用农家肥和自然水源灌溉，保持了玉米的天然风味。</li><li><strong>支持本地农业</strong>：购买农家玉米，您不仅享受到健康美味，也在支持本地农民，促进农业的可持续发展。</li></ol><p><b>价格：</b></p><p><b>19.99元/10根</b></p><p>选择我们的农家玉米，享受大自然的馈赠，同时为农民的辛勤工作点赞</p>', 'http://localhost:9090/files/1733197176032-玉米.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (5, '农家脆柿子', 9.99, 109, '水果', '<p><strong>农家脆柿子——自然馈赠，脆甜可口</strong></p><p>农家脆柿子，色泽鲜艳，口感脆甜，是自然赐予的美味。这些柿子在无污染的环境中生长，不使用化学肥料和农药，保证了其纯净和健康。</p><p><strong>特点：</strong></p><ol><li><strong>营养丰富</strong>：农家脆柿子含有丰富的维生素C、蛋白质、瓜氨酸、葡萄糖以及钙铁磷等矿物质<use></use>。</li><li><strong>健康益处</strong>：脆柿子具有润肠通便、健胃增食、补充营养、促进生长发育等功效<use></use>。</li><li><strong>天然种植</strong>：遵循传统农业智慧，采用有机方式种植，施用农家肥和生物制肥，人工除草和采果，确保果实自然成熟，含糖量高<use></use>。</li><li><strong>口感独特</strong>：与普通软柿子不同，脆柿子可直接削皮食用，甜而不腻，糖度可达14%～21%<use></use>。</li></ol><p><b>价格：</b></p><p><b>9.99元/3个</b></p><p>选择农家脆柿子，享受健康美味的同时，支持绿色农业，促进乡村经济发展。</p>', 'http://localhost:9090/files/1733197238122-柿子.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (6, '土鸡蛋', 9.90, 100, '畜牧', '<p><strong>农家土鸡蛋——自然散养，营养健康</strong></p><p>农家土鸡蛋，源自自然环境中散养的土鸡，这些鸡蛋具有以下特点：</p><ol><li><strong>蛋壳坚固</strong>：土鸡蛋蛋壳含钙量大，蛋壳厚且硬度大<use></use>。</li><li><strong>蛋清清澈粘稠</strong>：与普通鸡蛋相比，土鸡蛋蛋清更清澈且粘稠<use></use>。</li><li><strong>蛋黄凝结度高</strong>：土鸡蛋蛋黄凝结度高，浮在蛋清之上，不沉底，牙签可直立于蛋黄中间<use></use>。</li><li><strong>营养丰富</strong>：土鸡蛋中蛋白质、碳水化合物、胆固醇、钙、锌、铜、锰含量比普通鸡蛋略高<use></use>。</li><li><strong>无激素和抗生素残留</strong>：土鸡蛋在自然环境下生长，受人为因素干扰少，不存在皮革粉、肉骨粉和药渣等含有激素和抗生素的饲料等问题<use></use>。</li><li><strong>口感更佳</strong>：土鸡蛋中的脂肪含量更高，蛋黄较大，非常适合做煮鸡蛋和煎蛋，简单的烹调方法能将它优良的口感完全发挥出来<use></use>。</li></ol><p><b>价格：</b></p><p><b>9.90元/500g</b></p><p>选择农家土鸡蛋，您不仅享受到的是美味，更是对健康生活的追求和对自然环境的尊重。</p>', 'http://localhost:9090/files/1733197361276-土鸡蛋.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (7, '生姜', 8.90, 200, '蔬菜', '<p><strong>农家生姜——自然生长，健康原味</strong></p><p>农家生姜，种植于肥沃的土壤中，遵循自然农法，不施用化学肥料和农药，保证了生姜的纯净和自然。这些生姜具有以下特点：</p><ol><li><strong>新鲜出土</strong>：农家生姜在适宜的季节收获，根茎鲜嫩，组织柔嫩，纤维较少，辛辣味浓郁<use></use>。</li><li><strong>营养丰富</strong>：含有丰富的蛋白质、多种维生素、胡萝卜素、钙、铁、磷等，以及姜辣素、树脂、淀粉和纤维等<use></use>。</li><li><strong>健康益处</strong>：生姜能促进消化液分泌，增进食欲，具有镇吐、活血驱寒、杀菌和解毒等功效<use></use>。</li><li><strong>传统种植</strong>：采用传统农业智慧，施用农家肥和生物制肥，人工除草和采挖，保持了生姜的天然风味<use></use>。</li></ol><p><b>价格：</b></p><p><b>8.90元/500g</b></p><p>选择农家生姜，享受健康美味的同时，支持绿色农业，促进乡村经济发展。</p>', 'http://localhost:9090/files/1733197497215-生姜.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (8, '甘蔗', 10.00, 99, '水果', '<p><strong>农家甘蔗——自然甘甜，源自田间的甜蜜</strong></p><p>农家甘蔗，是自然生长在深厚肥沃土地上的甜蜜作物。这些甘蔗采用传统农业方法种植，不施用化学肥料和农药，确保了甘蔗的纯净和自然风味。</p><ol><li><strong>新鲜自然</strong>：农家甘蔗在适宜的季节收获，保证了甘蔗的新鲜和甜美，为您提供最天然的甜味享受<use></use>。</li><li><strong>营养丰富</strong>：甘蔗不仅含有丰富的蔗糖，还含有葡萄糖、果糖等，为您提供丰富的能量和营养<use></use>。</li><li><strong>多用途</strong>：除了直接食用，农家甘蔗还可用于制糖、酿酒等，其副产品如蔗渣可用于造纸、制作压缩板等，实现了资源的充分利用<use></use>。</li><li><strong>环保可持续</strong>：农家甘蔗的种植和加工过程注重环保和可持续性，减少了对环境的影响，同时也为农民提供了额外的收入来源<use></use>。</li></ol><p>选择农家甘蔗，您不仅能够享受到自然的甜蜜，也在支持环保和可持续农业的发展。</p>', 'http://localhost:9090/files/1733197568636-甘蔗.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (9, '龙川牛筋糕', 16.90, 100, '土特产', '<p><strong>龙川牛筋糕——传统风味，客家情怀</strong></p><ol><li><strong>历史传承</strong>：龙川牛筋糕最早记载于清朝年间，由陈顺升店生产，至今已有300多年的历史<use></use>。</li><li><strong>制作工艺</strong>：采用传统工艺和现代技术相结合，主要原料包括优质糯米粉、白砂糖、植物油等，制成的牛筋糕韧而不硬，软而不烂，入口有嚼劲且不粘牙<use></use>。</li><li><strong>风味特点</strong>：牛筋糕以其独特的口感和风味著称，色泽金黄透亮，富有弹性，香甜适口<use></use>。</li><li><strong>文化价值</strong>：不仅是美食，也是客家文化的传承，曾被作为名贵礼品带往东南亚及各地<use></use>。</li><li><strong>荣誉认证</strong>：产品多次荣获广东省优质产品称号，并被授予“东江特产”之美誉，远销东南亚一带<use></use>。</li></ol><p><b>价格：</b></p><p><b>16.90元/盒</b></p><p>龙川牛筋糕不仅是味觉的享受，更是文化的体验，每一口都是对传统客家美食的致敬。</p>', 'http://localhost:9090/files/1733197682726-牛筋糕.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (10, '潮汕红桃粿', 20.90, 200, '土特产', '<p>潮汕红桃粿是一种广东潮汕地区著名的汉族小吃，以其桃果造型而得名，象征着长寿，反映了祈福祈寿的愿望<use></use>。</p><ol><li><strong>外形美观</strong>：红桃粿外形像一个平面的红桃子，色泽鲜艳，给人以视觉上的享受<use></use>。</li><li><strong>馅料丰富</strong>：红桃粿的馅料甜咸皆有，常见的有糯米饭、香菇、虾米、去壳花生和少许芹菜粒等，精细的馅料考验刀工和耐心<use></use>。</li><li><strong>制作工艺</strong>：红桃粿的制作工艺并不复杂，但材料准备较为繁琐，需要将糯米饭与各种配料炒制后拌匀，再用特制的粿皮包裹，最后用红桃粿模具压成桃形<use></use>。</li><li><strong>文化意义</strong>：在潮汕地区，红桃粿不仅是美食，也是文化的一部分。每逢时年八节，几乎家家户户都会制作红桃粿用来祭拜神明，祈祷家人安康、生活顺心<use></use>。</li><li><strong>食用方法多样</strong>：红桃粿可以蒸熟后直接食用，也可以煎至两面金黄再食用，外脆里嫩，淋上卤鹅汁更是别有风味<use></use>。</li><li><strong>健康益处</strong>：红桃粿具有提高机体免疫功能，延缓衰老，防癌抗癌，降血压、降血脂、降胆固醇等功效<use></use>。</li></ol><p><b>价格：</b></p><p><b>20.90元/500g</b></p><p>潮汕红桃粿以其独特的风味和深厚的文化底蕴，成为了潮汕地区不可或缺的传统美食。</p>', 'http://localhost:9090/files/1733197770219-红桃粿.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (11, '西红柿', 5.90, 250, '蔬菜', '<p><strong>农家西红柿——自然成熟，鲜美多汁</strong></p><p>农家西红柿，种植在肥沃的土壤中，采用传统的农业方法，不施用化学肥料和农药，确保了西红柿的自然成熟和纯正口感。</p><ol><li><strong>季节性种植</strong>：春西红柿在早春2月份播种育苗，秋冬季的在7月中下旬到8月上中旬播种，冬春那一茬在11月上旬至12月上旬播种<use></use>。</li><li><strong>成熟季节</strong>：西红柿属于夏季时令蔬菜，一般3~4月份种植的需要50~60天成熟，5~6月份种植的需要45~50天成熟，7~8月份气温较高，40天左右即可采收<use></use>。</li><li><strong>种植技术</strong>：选择适合西红柿生长的土壤，理想的土壤为疏松、肥沃、富含有机质的壤土。同时要选择光照充足的地方，避免阴暗潮湿的环境。种植前对种子进行温水浸种，这样不仅可以打破种子休眠，促进发芽，还有一定的灭菌杀菌的作用<use></use>。</li><li><strong>收获期</strong>：西红柿收获期一般在100天左右，大棚栽培可以提前10天左右<use></use>。</li></ol><p><b>价格：</b></p><p><b>5.90元/kg</b></p><p>选择农家西红柿，享受自然成熟带来的鲜美滋味，同时支持可持续农业的发展</p>', 'http://localhost:9090/files/1733197873092-西红柿.png', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (12, '农家甜柚子', 20.00, 100, '水果', '<p><strong>农家甄选甜柚——自然成熟，甜润多汁</strong></p><p>农家甜柚子，以其自然成熟和甜美多汁而闻名。这些柚子在适宜的气候和土壤条件下生长，不使用化学肥料和农药，保留了柚子的天然风味和营养价值。</p><ol><li><strong>品种多样</strong>：农家甜柚子包括多种特色品种，如广丰马家柚、怀化雪峰柚、江永香柚等，各具特色，甜度和风味各异<use></use>。</li><li><strong>自然成熟</strong>：农家甜柚子在充足的阳光和适宜的气候下自然成熟，保证了果实的新鲜和甜美。</li><li><strong>营养丰富</strong>：富含多种维生素和矿物质，具有较高的营养价值。</li><li><strong>传统种植</strong>：采用传统农业方法种植，注重环保和可持续性，减少对环境的影响。</li><li><strong>健康益处</strong>：甜柚子具有清热解暑、生津止渴的功效，适合各个年龄段的人群食用。</li></ol><p><b>价格：</b></p><p><b>20元/3个</b></p><p>选择农家甜柚子，享受自然的馈赠，支持传统农业，品味健康生活。</p>', 'http://localhost:9090/files/1733197948195-柚子.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (13, '糯米糕', 10.00, 90, '土特产', '<p><strong>农家糯米糕——传统风味，软糯香甜</strong></p><p>农家糯米糕，是一种传统的甜点，以其软糯的口感和香甜的风味深受人们喜爱。以下是其主要特点：</p><ol><li><p><strong>传统工艺</strong>：农家糯米糕采用传统制作方法，通常包括糯米浸泡、蒸煮、搅拌和成型等步骤，保留了糯米的原始风味<use></use>。</p></li><li><p><strong>食材简单</strong>：主要原料为糯米和白糖，有的还会加入红糖、红枣等增加风味和营养价值<use></use>。</p></li><li><p><strong>营养丰富</strong>：糯米糕含有大量的碳水化合物，能快速提供能量，含有蛋白质、B族维生素等，有助于美容养颜、促进消化、增强免疫力<use></use>。</p></li><li><p><strong>食用方便</strong>：糯米糕可以提前制作，冷藏保存，食用时只需简单加热，方便快捷<use></use>。</p></li><li><p><strong>健康提示</strong>：糯米糕虽好，但糖尿病患者和消化功能较弱的人群应适量食用，以免引起血糖波动或消化不良<use></use>。</p></li></ol><p><b>价格：</b></p><p><b>10元/盒</b></p><p>农家糯米糕，不仅是美味的甜品，也是承载传统和文化的佳品。</p>', 'http://localhost:9090/files/1733198044720-糯米糕.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (14, '甄选花生', 11.99, 180, '土特产', '<p><strong>农家花生——自然种植，营养丰富</strong></p><p>农家花生，种植在疏松的沙质土壤中，采用传统的农业方法，不施用化学肥料和农药，确保了花生的自然成熟和纯正口感。</p><ol><li><p><strong>种植时间</strong>：花生一般可以在春季和秋季种植，北方春花生的播种期一般为4月中旬至5月，南方则在2月中旬至3月播种<use></use>。</p></li><li><p><strong>种植方法</strong>：选择排水良好、土层深厚肥沃的土壤，施用优质农家肥，采用起垄种植，避免雨季时被淹而形成病害<use></use>。</p></li><li><p><strong>收获季节</strong>：花生的收获时间因地区和品种而异，北方地区一般在9月份收获，南方则在10月份左右收获<use></use>。</p></li><li><p><strong>品种多样</strong>：农家花生有多种品种，如高油酸花生、普通型花生、珍珠豆型花生等，各具特色，满足不同需求<use></use>。</p></li><li><p><strong>用途广泛</strong>：花生不仅可以直接食用，还可用于榨油、制作花生酱、做菜、炖汤等，副产品如花生饼粕、花生壳等也有多种用途<use></use>。</p></li></ol><p><b>价格：</b></p><p><b>11.99元/kg</b></p><p>选择农家花生，享受自然的馈赠，支持传统农业，品味健康生活。</p>', 'http://localhost:9090/files/1733198115022-花生.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (15, '农家饲养鸭', 60.00, 45, '畜牧', '<ol><li><p><strong>饲养方式</strong>：农家饲养鸭主要有放牧饲养、全舍饲和半舍饲三种方式<use></use>。放牧饲养利用鸭的觅食能力，节约饲料成本，增强鸭体质<use></use>。全舍饲则是将饲养过程全部在鸭舍内进行，管理严格，需要设置饮水和排水系统<use></use>。半舍饲结合了舍饲和放牧的特点，固定在鸭舍、陆上运动场和水上运动场，不外出放牧<use></use>。</p></li><li><p><strong>鸭舍建造</strong>：选择土壤透水性好、背风向阳的场地建鸭舍，场地整成斜坡地防止积水，水源充足，最好建在靠近池塘的地方<use></use>。</p></li><li><p><strong>品种选择</strong>：肉用鸭性情温顺，适应性强，育肥性能好，适宜密集化群饲圈养<use></use>。</p></li><li><p><strong>饲料配方</strong>：根据鸭子的不同生长阶段，提供不同的饲料配方，以满足其营养需求<use></use>。</p></li><li><p><strong>饲喂方法</strong>：雏鸭出壳24小时内及时给水，之后提供饲料，1～3日龄每小时喂一次，4日龄后饲料少给勤添，促使鸭昼夜自由采食，供给充足清洁饮水<use></use>。</p></li><li><p><strong>环境控制</strong>：雏鸭1～3日龄舍温保持30～31℃，之后逐渐降低，加强通风、透光，保持空气清新、饲料新鲜、饮水清洁<use></use>。<br/></p><p><b>价格：</b></p><p><b>60元/只</b></p></li></ol>', 'http://localhost:9090/files/1733198227938-鸭子.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (16, '红豆', 7.99, 66, '粮食', '<p><strong>农家红豆——源自土地的关怀，温暖每一颗心</strong></p><p>农家红豆，是自然恩赐与农民辛勤劳作的结晶。这些红豆在肥沃的土壤中自然生长，不施化学肥料，不打农药，保证了每一粒红豆的纯净与健康。</p><p><strong>特点：</strong></p><ol><li><strong>天然健康</strong>：农家红豆含有丰富的蛋白质、纤维素和微量元素，是天然的营养宝库。</li><li><strong>助农增收</strong>：购买农家红豆，您不仅享受到健康食品，也在支持农民的生计，促进当地农业的可持续发展。</li><li><strong>传统种植</strong>：遵循传统农业种植方式，保持了红豆的原始风味和营养价值。</li></ol><p><b>价格：</b></p><p><b>7.99元/250g</b></p><p>选择农家红豆，您在享受健康美味的同时，也在为农民的辛勤工作点赞，共同助力农村的繁荣发展。</p>', 'http://localhost:9090/files/1733209285140-红豆.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (17, '三华李', 5.99, 200, '水果', '<p><strong>农家三华李——岭南佳果，助农增收</strong></p><p>农家三华李，源自广东翁源县三华镇，拥有近五百年的种植历史<use></use>。这些李子在适宜的气候和土壤条件下生长，不施化学肥料，不打农药，保证了三华李的纯净与健康。</p><p><strong>特点：</strong></p><ol><li><strong>色泽艳丽</strong>：三华李果红皮红肉，色质艳丽，个大肉厚，肉质爽脆，酸甜可口，气味芳香<use></use>。</li><li><strong>营养丰富</strong>：果实含糖、蛋白质、胡萝卜素、核黄素等，每100毫升果汁中含维生素C 17.5－28.7毫克，风味品质极佳，营养价值高<use></use>。</li><li><strong>助农增收</strong>：购买农家三华李，您不仅享受到健康食品，也在支持农民的生计，促进当地农业的可持续发展<use></use>。</li><li><strong>文化传承</strong>：三华李不仅是美食，也是客家文化的传承，曾被作为名贵礼品带往东南亚及各地<use></use>。</li></ol><p><b>价格：</b></p><p><b>5.99元/500g</b></p><p>选择农家三华李，您在享受健康美味的同时，也在为农民的辛勤工作点赞，共同助力农村的繁荣发展</p>', 'http://localhost:9090/files/1733209316621-三华李.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (18, '农家甜粽子', 5.00, 200, '粮食', '<p><strong>农家甜粽子——传统手作，甜蜜分享</strong></p><p>农家甜粽子，是传统手艺与乡土温情的结合。选用优质糯米和天然粽叶，手工包裹，每一颗粽子都蕴含着农家的心意和对传统美食的尊重。</p><p><strong>特点：</strong></p><ol><li><strong>天然原料</strong>：采用农家自产糯米，搭配天然粽叶，不添加任何化学成分，保证了粽子的自然风味。</li><li><strong>甜而不腻</strong>：甜粽子内馅多采用红豆、红枣等天然甜味食材，甜而不腻，营养丰富。</li><li><strong>助农增收</strong>：购买农家甜粽子，您不仅享受到传统美食，也在支持农民的生计，促进当地农业的可持续发展。</li><li><strong>节日佳品</strong>：甜粽子是端午节的传统食品，也是传递节日祝福和文化的重要载体。</li></ol><p><b>价格：</b></p><p><b>5元/个（3个起包邮）</b></p><p>选择农家甜粽子，您在享受美味的同时，也在为农民的辛勤工作点赞，共同助力农村的繁荣发展。</p>', 'http://localhost:9090/files/1733209431895-粽子.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (19, '甄选荷兰豆', 8.00, 100, '蔬菜', '<p><strong>甄选荷兰豆——自然鲜美，助农增收</strong></p><p>农家甄选荷兰豆，采用传统种植方式，生长在肥沃的土壤中，不施化肥和农药，确保每一根豆荚的天然与健康。</p><p><strong>特点：</strong></p><ol><li><strong>鲜嫩可口</strong>：荷兰豆荚色泽鲜绿，口感脆嫩，清甜可口，是健康饮食的理想选择。</li><li><strong>营养丰富</strong>：富含蛋白质、维生素C和膳食纤维，能够增强免疫力，促进消化，适合各类人群食用。</li><li><strong>助农增收</strong>：购买农家荷兰豆，您不仅享受到新鲜美味，也在支持农民的生计，助力农村经济发展。</li></ol><p><b>价格：</b></p><p><b>8.0元/500g</b></p><p>选择农家甄选荷兰豆，享受自然的美味，同时为农民的辛勤付出点赞，共同推动乡村振兴</p>', 'http://localhost:9090/files/1733209572519-荷兰豆.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (20, '手工花篮', 10.00, 100, '手工艺品', '<p><strong>手工花篮——匠心独运，自然之美</strong></p><p>每一只手工花篮都是传统工艺与现代美学的结晶，由技艺精湛的手工艺人精心编织而成。</p><p><strong>特点：</strong></p><ol><li><strong>手工编织</strong>：采用天然材料，如柳条、藤条或竹子，手工编织，每个花篮都独一无二。</li><li><strong>环保实用</strong>：不仅美观，而且耐用，适合放置鲜花或干花，为家居装饰增添自然气息。</li><li><strong>文化传承</strong>：购买手工花篮，您支持的不仅是一个产品，更是对传统手工艺的传承和对工匠精神的尊重。</li></ol><p><b>价格：</b></p><p><b>10.0元/个</b></p><p>选择手工花篮，为您的生活空间带来一抹温馨与雅致，同时支持手工艺人们的生计，促进文化多样性的保护。</p>', 'http://localhost:9090/files/1733209658873-手工花篮.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (21, '手工红薯粉', 14.99, 100, '粮食', '<p><strong>手工红薯粉——传统工艺，家乡味道</strong></p><p>手工红薯粉，传承着世代农家的智慧与匠心。每一根粉条都是手工制作，色泽黄亮，身干条细，韧性好，食之柔软爽口。</p><p><strong>特点：</strong></p><ol><li><strong>传统手作</strong>：采用传统工艺，无任何化学添加剂，保留了红薯的自然风味和营养<use></use>。</li><li><strong>绿色食品</strong>：纯绿色食品，天然的农家产品，健康又美味<use></use>。</li><li><strong>助农增收</strong>：购买手工红薯粉，支持传统手工艺，助力农民增收，促进乡村振兴<use></use>。</li></ol><p><b>价格：</b></p><p><b>14.99元/包</b></p><p>选择手工红薯粉，不仅是对美味的追求，也是对传统手艺的支持和传承。</p>', 'http://localhost:9090/files/1733209728988-手工红薯粉.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (22, '野生蜂蜜', 99.00, 50, '土特产', '<p><strong>野生蜂蜜——自然馈赠，甜蜜纯净</strong></p><p>采集自深山野林的野生蜂蜜，未经人为干预，保留了最原始的自然风味。</p><p><strong>特点：</strong></p><ol><li><strong>天然纯净</strong>：源自野生蜂群，无添加，无污染，每一滴都蕴含大自然的精华。</li><li><strong>营养丰富</strong>：含有多种维生素、矿物质和氨基酸，具有增强免疫力和改善睡眠等健康益处。</li><li><strong>助农增收</strong>：购买野生蜂蜜，支持传统养蜂业，帮助养蜂人增收，促进生态农业发展。</li></ol><p><b>价格：</b></p><p><b>99元/瓶</b></p><p>选择野生蜂蜜，享受自然的甜蜜，同时为保护传统养蜂文化和生态环境贡献力量。</p>', 'http://localhost:9090/files/1733209863184-野生蜂蜜.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (23, '老坛酸菜', 59.90, 40, '蔬菜', '<p><strong>老坛酸菜——传统发酵，酸香独特</strong></p><p>老坛酸菜，采用传统工艺在红泥老坛中腌制，经过六个月以上的自然发酵，不含任何人工添加，保留了最纯正的酸香口感。</p><p><strong>特点：</strong></p><ol><li><strong>自然发酵</strong>：真正的老坛酸菜，通过长时间自然发酵，酸味纯正，久煮依然脆爽<use></use>。</li><li><strong>传统工艺</strong>：沿袭古法炮制，挑选陶坛，调和五味，将芥菜浸入老卤水充分发酵，传承了四川人民的智慧<use></use>。</li><li><strong>助农增收</strong>：购买老坛酸菜，支持传统发酵食品的制作，助力当地农民增收，促进农业可持续发展<use></use>。</li></ol><p><b>价格：</b></p><p><b>59.9元/罐</b></p><p>选择老坛酸菜，享受传统美食的同时，也为保护和传承传统手工艺贡献一份力量。</p><p><use></use></p>', 'http://localhost:9090/files/1733209969736-老坛酸菜.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (24, '甄选大葱', 20.00, 97, '蔬菜', '<p><strong>农家大葱——绿色种植，调味佳品</strong></p><p>农家大葱，自然生长在肥沃的土壤中，采用传统绿色种植方式，无化学肥料和农药，保证了大葱的新鲜和纯正口感。</p><p><strong>特点：</strong></p><ol><li><strong>新鲜脆嫩</strong>：农家大葱叶绿茎白，质地脆嫩，口感辛辣适中，是日常烹饪中不可或缺的调味品。</li><li><strong>营养丰富</strong>：富含多种维生素和矿物质，具有增进食欲、促进消化的健康益处。</li><li><strong>助农增收</strong>：购买农家大葱，您不仅享受到新鲜食材，也在支持农民的生计，助力乡村经济发展。</li></ol><p><b>价格：</b></p><p><b>20元/把</b></p><p>选择农家大葱，为您的菜肴增添自然风味，同时为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733219843269-大葱.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (25, '橄榄', 9.99, 200, '水果', '<p><strong>农家橄榄——自然成熟，健康之选</strong></p><p>农家橄榄，生长在适宜的气候和土壤条件下，采用传统农业方法种植，不施化学肥料和农药，保证了橄榄的自然成熟和纯正口感。</p><p><strong>特点：</strong></p><ol><li><strong>天然健康</strong>：农家橄榄含有丰富的维生素和矿物质，是天然的健康食品<use></use>。</li><li><strong>助农增收</strong>：购买农家橄榄，您不仅享受到健康食品，也在支持农民的生计，促进当地农业的可持续发展<use></use>。</li><li><strong>传统种植</strong>：遵循传统农业智慧，保持了橄榄的原始风味和营养价值<use></use>。</li></ol><p><b>价格：</b></p><p><b>9.99元/kg</b></p><p>选择农家橄榄，享受自然馈赠的同时，为农民的辛勤工作点赞，共同助力农村的繁荣发展。</p>', 'http://localhost:9090/files/1733219900481-橄榄.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (26, '手工编织草帽', 9.90, 40, '手工艺品', '<p><strong>手工编织草帽——夏日遮阳，传统工艺</strong></p><p>这顶手工编织草帽，采用天然材料，由当地工匠精心编织而成，不仅为您带来夏日的凉爽，也展现了传统手工艺的魅力。</p><p><strong>特点：</strong></p><ol><li><strong>天然材料</strong>：使用天然植物纤维，如稻草或棕榈叶，环保且透气。</li><li><strong>遮阳防晒</strong>：宽边设计有效遮挡阳光，保护您免受紫外线伤害。</li><li><strong>文化传承</strong>：购买手工草帽，您在享受实用的同时，也在支持传统手工艺的传承和农民的生计。</li></ol><p><b>价格：</b></p><p><b>9.90元/顶</b></p><p>选择一顶手工草帽，为您的夏日出行增添一份舒适与风情，同时支持手工艺的发展。</p>', 'http://localhost:9090/files/1733219981612-草帽.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (27, '剪纸', 10.90, 39, '手工艺品', '<p><strong>手工剪纸——传统艺术，纸上生花</strong></p><p>每一张手工剪纸都是对传统艺术的致敬，由技艺精湛的手工艺人精心剪裁而成。</p><p><strong>特点：</strong></p><ol><li><strong>手工艺传承</strong>：承载中国千年剪纸艺术，每一件作品都是独一无二的。</li><li><strong>精美细致</strong>：图案精美，线条流畅，展现了剪纸艺术的精细与雅致。</li><li><strong>文化价值</strong>：不仅是装饰品，更是传递节日祝福和文化的重要载体。</li></ol><p><b>价格：</b></p><p><b>10.90元/张</b></p><p>选择手工剪纸，为您的生活空间增添一抹传统文化的色彩，同时支持传统手工艺的传承。</p>', 'http://localhost:9090/files/1733220053752-剪纸.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (28, '甄选龙眼', 9.90, 200, '水果', '<p><strong>龙眼——甜蜜滋味，助农之选</strong></p><p>龙眼，又名桂圆，是亚热带地区的珍宝。这些果实在温暖湿润的气候中自然成熟，无需过多人工干预，保证了其天然和健康的品质。</p><p><strong>特点：</strong></p><ol><li><strong>自然成熟</strong>：龙眼喜欢温暖，忌寒冷，在年平均温度为20-22摄氏度的地区生长最为适宜<use></use>。</li><li><strong>营养丰富</strong>：果实富含多种维生素和矿物质，有益脾、健脑的作用<use></use>。</li><li><strong>助农增收</strong>：购买龙眼，您不仅享受到了甜美的果实，也在支持当地农民的生计，促进农业的可持续发展<use></use>。</li></ol><p><b>价格：</b></p><p><b>9.90元/kg</b></p><p>选择龙眼，品尝自然的馈赠，同时为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733220143706-龙眼.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (29, '甄选黄豆', 18.90, 120, '粮食', '<p><strong>山区黄豆——自然耕作，营养之源</strong></p><p>这些黄豆生长在远离污染的山区，由当地农民以传统方式种植，不依赖化学肥料，保留了豆子的自然风味和营养价值。</p><p><strong>特点：</strong></p><ol><li><strong>纯净生长</strong>：山区空气清新，水源纯净，黄豆在这种环境中自然生长，品质上乘。</li><li><strong>营养丰富</strong>：富含蛋白质和膳食纤维，是健康饮食中不可或缺的食材。</li><li><strong>助农增收</strong>：购买山区黄豆，您不仅享受到健康食品，也在支持贫困山区农民的生计，助力脱贫攻坚。</li></ol><p><b>价格：</b></p><p><b>18.90元/kg</b></p><p>选择山区黄豆，享受健康美味的同时，为山区农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733220242439-黄豆.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (30, '胡萝卜', 7.99, 100, '蔬菜', '<p><strong>山区胡萝卜——自然生长，助力脱贫</strong></p><p>这些胡萝卜生长在远离污染的山区，由当地农民以传统方式精心培育。它们在清新的空气中自然成熟，不仅为农民带来希望，也为消费者提供健康。</p><p><strong>特点：</strong></p><ol><li><strong>绿色健康</strong>：山区胡萝卜在无污染的环境中生长，是真正的绿色食品，富含胡萝卜素和多种维生素<use></use>。</li><li><strong>助农增收</strong>：购买山区胡萝卜，您不仅享受到新鲜食材，也在支持贫困山区农民的生计，助力乡村振兴<use></use>。</li><li><strong>传统种植</strong>：遵循传统农业智慧，保持了胡萝卜的原始风味和营养价值<use></use>。</li></ol><p><b>价格：</b></p><p><b>7.99元/kg</b></p><p>选择山区胡萝卜，享受健康美味的同时，为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733220352100-胡萝卜.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (31, '甄选小米', 19.99, 70, '粮食', '<p><strong>农家小米——自然馈赠，营养之选</strong></p><p>农家小米，源自绿色田野，由辛勤的农民手工种植，每一粒都凝聚着阳光和土地的精华。</p><p><strong>特点：</strong></p><ol><li><strong>天然健康</strong>：小米在无污染的环境中生长，不使用化学肥料和农药，保证了其天然纯净。</li><li><strong>营养丰富</strong>：富含蛋白质、维生素和矿物质，是日常饮食中的营养补充。</li><li><strong>助农增收</strong>：购买农家小米，您不仅享受到健康食品，也在支持农民的生计，促进农业的可持续发展。</li></ol><p><b>价格：</b></p><p><b>19.99元/500g</b></p><p>选择农家小米，享受健康美味的同时，为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733227305923-小米.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (32, '东北冻梨', 10.00, 100, '水果', '<p><strong>东北冻梨——冬日里的甜蜜奇迹</strong></p><p>东北冻梨，是东北地区特有的冬季水果。经过自然冷冻，这些梨子由内而外发生了神奇的变化，外表黢黑，内心却藏着甜美的惊喜。</p><p><strong>特点：</strong></p><ol><li><strong>自然冷冻</strong>：冻梨由花盖梨、秋白梨等品种经过零下几十度的低温冷冻而成，风味独特<use></use>。</li><li><strong>酸甜可口</strong>：冻梨解冻后，果肉绵密、汁水充沛，酸甜爽口，是东北人冬季的解腻“良药”<use></use>。</li><li><strong>传统美食</strong>：冻梨不仅是东北人的传统美食，也承载着东北地区的文化和记忆<use></use>。</li></ol><p><b>价格：</b></p><p><b>10元3个</b></p><p>选择东北冻梨，品尝这份来自冰天雪地的自然馈赠，同时支持东北农民的传统种植与文化传承。</p>', 'http://localhost:9090/files/1733227398736-冻梨.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (33, '江西蜜桔', 7.99, 120, '水果', '<p><strong>江西蜜桔——甘甜如蜜，果中珍品</strong></p><p>江西蜜桔，以其皮薄核少、汁多渣少、色泽金黄、甜酸适口、营养丰富而闻名。这些蜜桔在江西的适宜气候和肥沃土壤中自然成熟，是柑桔中的精品。</p><p><strong>特点：</strong></p><ol><li><strong>品质特性</strong>：江西蜜桔色泽金黄，皮薄多汁，甜而不腻，香气浓郁，是水果中的佳品<use></use>。</li><li><strong>营养丰富</strong>：含有人体必需的多种维生素和矿物质，兼具营养与保健功效<use></use>。</li><li><strong>助农增收</strong>：购买江西蜜桔，您不仅享受到美味的果实，也在支持当地农民的生计，助力农业发展。</li></ol><p><b>价格：</b></p><p><b>7.99元/kg</b></p><p>选择江西蜜桔，品尝自然的甜蜜，同时为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733227492295-蜜桔.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (34, '鹰嘴桃', 10.00, 200, '水果', '<p><strong>鹰嘴桃——果中珍品，清甜爽口</strong></p><p>鹰嘴桃，以其独特的外形和卓越口感而闻名。果实大圆形，尾部像“鹰嘴”，色泽鲜亮，果大形美，肉质脆嫩，清甜爽口，带有蜜味<use></use>。</p><p><strong>特点：</strong></p><ol><li><strong>外形美观</strong>：果面绿色，阳面有红晕，外形美观，果肉绿红色，由内而外变红<use></use>。</li><li><strong>营养丰富</strong>：含有丰富的维生素和矿物质，具有较高的营养价值<use></use>。</li><li><strong>助农增收</strong>：购买鹰嘴桃，您不仅享受到美味的果实，也在支持当地农民的生计，助力农业发展<use></use>。</li></ol><p><b>价格：10元/kg</b></p><p>选择鹰嘴桃，品尝自然的甜蜜，同时为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733228884755-鹰嘴桃.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (35, '山西红富士苹果', 30.00, 140, '水果', '<p><strong>山西红富士苹果——黄土高原的甜蜜馈赠</strong></p><p>山西红富士苹果，来自黄土高原的自然恩赐，以其色泽鲜艳、口感爽脆而著称。</p><p><strong>特点：</strong></p><ol><li><strong>色泽鲜艳</strong>：果面通红，大部分果面着橘红色霞，覆有鲜红色条纹<use></use>。</li><li><strong>口感爽脆</strong>：皮薄肉酥、清甜多汁、醇香清脆，每咬一口水分就在嘴里爆开<use></use>。</li><li><strong>营养丰富</strong>：含有丰富的蛋白质、胡萝卜素等营养物质，对于预防癌症、降血压有着很大的功效<use></use>。</li><li><strong>助农增收</strong>：购买山西红富士苹果，您不仅享受到美味的果实，也在支持当地农民的生计，助力农业发展<use></use>。</li></ol><p><b>价格：30元/箱</b></p><p>选择山西红富士苹果，品尝自然的甜蜜，同时为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733229022370-红富士苹果.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (36, '荔枝（桂味）', 40.00, 100, '水果', '<p><strong>荔枝——南国佳果，甜蜜多汁</strong></p><p>荔枝，以其晶莹剔透的果肉和甜美的果汁而闻名，是夏季里最受欢迎的水果之一。</p><p><strong>特点：</strong></p><ol><li><strong>新鲜多汁</strong>：荔枝果肉饱满，汁液丰富，甜中带微酸，风味独特。</li><li><strong>营养丰富</strong>：含有多种维生素和矿物质，尤其是维生素C的含量非常高，有助于增强免疫力。</li><li><strong>助农增收</strong>：购买荔枝，您不仅享受到了美味的果实，也在支持荔枝种植地区的农民，促进当地经济发展。</li></ol><p><b>价格：40元/箱</b></p><p>选择荔枝，品尝夏季的甜蜜，同时为农民的辛勤工作贡献一份力量</p>', 'http://localhost:9090/files/1733229201758-荔枝.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (37, '甄选茄子', 10.00, 100, '蔬菜', '<p><strong>农家茄子——田间至宝，紫色诱惑</strong></p><p>农家茄子，自然生长在肥沃的土壤中，由农民精心培育，不施化学肥料，保证了茄子的天然风味和营养价值。</p><p><strong>特点：</strong></p><ol><li><strong>新鲜采摘</strong>：农家茄子新鲜采摘，肉质紧实，口感细腻。</li><li><strong>营养丰富</strong>：含有丰富的维生素和矿物质，尤其是维生素P，有助于增强血管弹性。</li><li><strong>助农增收</strong>：购买农家茄子，您不仅享受到健康食品，也在支持农民的生计，促进农业的可持续发展。</li></ol><p><b>价格：10元/kg</b></p><p>选择农家茄子，享受健康美味的同时，为农民的辛勤工作贡献一份力量。</p>', 'http://localhost:9090/files/1733229344208-茄子.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (38, '四川腊肉', 20.00, 100, '土特产', '<p><strong>四川腊肉——传统风味，烟熏香气</strong></p><p>四川腊肉，以其独特的烟熏香气和麻辣口感著称，是四川传统美食的代表之一。<use></use></p><p><strong>特点：</strong></p><ol><li><strong>烟熏工艺</strong>：采用传统的烟熏方法，使用柏树枝条树叶进行熏制，赋予腊肉独特的香气。<use></use></li><li><strong>麻辣风味</strong>：在腌制过程中加入辣椒和花椒，使得四川腊肉具有麻辣咸香的丰富口感。<use></use></li><li><strong>肉质鲜美</strong>：选用优质猪肉，经过精心腌制和熏制，肉质紧实而有嚼劲，风味独特。<use></use></li></ol><p><b>价格：20元/根</b></p><p>选择四川腊肉，不仅品尝到传统工艺的美味，也在支持当地农民的生计和传统手工艺的传承。</p>', 'http://localhost:9090/files/1733229471883-腊肉.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (39, '腊肠', 39.90, 112, '土特产', '<p><strong>传统腊肠——风味独特，肉质鲜美</strong></p><p>腊肠，是中国传统肉制品之一，以其独特的香味和风味深受人们喜爱。</p><p><strong>特点：</strong></p><ol><li><strong>手工制作</strong>：精选优质肉料，手工灌制，保留了肉质的鲜美和弹性。</li><li><strong>香味浓郁</strong>：经过腌制和风干，腊肠散发出特有的香气，风味独特。</li><li><strong>食用方便</strong>：可以直接蒸煮食用，也可以作为烹饪的配料，增添菜肴风味。</li></ol><p><b>价格：39.9/500g</b></p><p>选择腊肠，享受传统美食带来的温馨与满足，同时支持传统手工艺的传承。</p>', 'http://localhost:9090/files/1733229607735-腊肠.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (40, '客家甜粄', 9.90, 123, '土特产', '<p><strong>客家甜粄——传统年味，甜蜜糯香</strong></p><p>客家甜粄，又称甜糍或甜粘糕，是客家人传统的年节美食。它以糯米粉为主要原料，加入红糖及适量白糖，蒸熟后口感软糯，甜度适中，非常受大家欢迎<use></use>。</p><p><strong>特点：</strong></p><ol><li><strong>传统工艺</strong>：甜粄是客家年糕的一种，客家人过春节时会制作，有“不蒸甜粄不过年，没有甜粄不成礼”的说法<use></use>。</li><li><strong>寓意吉祥</strong>：在正月初一至初五，人们都会吃甜粄来祈求新的一年里甜甜蜜蜜，健康平安<use></use>。</li><li><strong>口感独特</strong>：甜粄蒸熟后，直接食用糯糯叽叽，香味浓郁；也可以煎制，外酥内糯，口感软糯柔顺<use></use>。</li></ol><p><b>价格：9.9元/500g</b></p><p>选择客家甜粄，品尝传统美食的同时，也为客家人对新的一年美好的祝愿增添了一份甜蜜。</p>', 'http://localhost:9090/files/1733229722960-客家甜粄.jpg', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (41, '五指毛桃', 59.90, 30, '土特产', '<p><strong>五指毛桃——岭南草本，健脾补肺</strong></p><p>五指毛桃，别名五指牛奶、土黄芪，是岭南地区特有的草本植物。其根入药，全年可采，洗净切片后晒干使用。</p><p><strong>特点：</strong></p><ol><li><strong>药用价值</strong>：五指毛桃具有健脾补肺、行气利湿、舒筋活络的功效<use></use>。</li><li><strong>食疗养生</strong>：既能补虚治病，又能食疗养生，其汤香气浓郁，类似椰奶，有补虚强壮的作用<use></use>。</li><li><strong>传统应用</strong>：在中医中，五指毛桃用于治疗肺结核咳嗽、慢性支气管炎、风湿性关节炎等多种病症<use></use>。</li></ol><p><b>价格：59.9元/500g</b></p><p>选择五指毛桃，享受传统草本的滋养，同时支持岭南地区的草药文化和农民的生计。</p>', 'http://localhost:9090/files/1733229844185-五指毛桃.png', '2024-12-08 15:51:55');
INSERT INTO `products` VALUES (42, '腐竹', 13.99, 120, '土特产', '<p><strong>腐竹——植物蛋白精华，素食者的佳肴</strong></p><p>腐竹，又称为豆腐皮，是由豆浆加热煮沸后表面形成的一层薄膜，经过干燥制成的食品。它色泽黄白，油光透亮，含有丰富的蛋白质及多种营养成分。</p><p><strong>特点：</strong></p><ol><li><strong>营养丰富</strong>：腐竹含有大量的蛋白质和少量的脂肪，是一种低脂肪、高蛋白的健康食品。</li><li><strong>易于吸收</strong>：腐竹中的蛋白质经过加工后更易于人体消化吸收，适合各个年龄段的人群。</li><li><strong>烹饪多样</strong>：腐竹可以凉拌、热炒、炖汤或作为火锅配料，能够吸收各种食材的味道，烹饪方式多样。</li><li><strong>素食佳品</strong>：由于其口感和肉类相似，腐竹是素食者的重要蛋白质来源之一。</li><li><strong>健康益处</strong>：腐竹含有的卵磷脂对降低胆固醇、预防心血管疾病有一定的帮助。</li></ol><p>价格：13.99元/500g</p><p>选择腐竹，享受健康美味的同时，也能感受到中国传统豆制品的独特魅力。</p>', 'http://localhost:9090/files/1733229960741-腐竹.jpg', '2024-12-08 15:51:55');

-- ----------------------------
-- Table structure for projectapply
-- ----------------------------
DROP TABLE IF EXISTS `projectapply`;
CREATE TABLE `projectapply`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` int NULL DEFAULT NULL COMMENT '用户ID',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '项目说明',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '申请时间',
  `file` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '申请资料',
  `status` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核状态',
  `descr` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '审核说明',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '扶贫项目申请表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of projectapply
-- ----------------------------
INSERT INTO `projectapply` VALUES (5, 1, '附件是我的农家乐申请策划书等资料，已经补全材料，希望审核通过！！！', '2023-12-26', 'http://localhost:9090/files/1703585085364-张三扶贫农家乐申请.7z', '审核通过', '资料齐全，允许申请');

-- ----------------------------
-- Table structure for stories
-- ----------------------------
DROP TABLE IF EXISTS `stories`;
CREATE TABLE `stories`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '序号',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '故事标题',
  `excerpt` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '故事摘要',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '故事全文内容',
  `time` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '发布时间',
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '故事配图URL',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '扶贫助农故事表' ROW_FORMAT = Dynamic;

-- ----------------------------
