-- ----------------------------
-- Table structure for video
-- ----------------------------
DROP TABLE IF EXISTS `video`;
CREATE TABLE `video`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '序号',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '视频标题',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '视频描述',
  `videoUrl` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '视频链接',
  `uploadTime` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '上传时间',
  `coverImage` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '视频封面',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of video
-- ----------------------------
INSERT INTO `video` VALUES (1, '初心，在路上——从脱贫攻坚走向乡村振兴', '一道道山来一道道梁耶……掬一捧黄土，待旧貌换新颜。 2021年2月25日，中国向全世界庄严宣告，在迎来中国共产党成立一百周年的重要时刻，我国脱贫攻坚战取得了全面胜利，创造了又一个彪炳史册的人间奇迹。 天津甘肃，一东一西，相距千里，从“脱贫攻坚”走向“乡村振兴”，津门与陇原紧密牵手，情暖人心。', 'http://localhost:9090/files/1733739212286-扶贫助农1.mp4', '2024-12-09 18:14:36', 'http://localhost:9090/files/1733739271306-助农视频封面1.jpg');
INSERT INTO `video` VALUES (2, '习近平的扶贫故事第一集：一片良田', '一片良田，见证了一个年代。\n1969年，15岁的习近平从北京来到黄土高原，成为梁家河插队知青的一员。“那时候，我和乡亲们都住在土窑洞里，睡的是土炕，乡亲们生活十分困苦，经常是几个月吃不到一块肉。我很期盼的一件事，就是让乡亲们饱餐一顿肉，并且今后能够经常吃肉。”多年后，习近平回忆起这段岁月，仍然感慨万千。\n在这里，他带领乡亲们挖井、打坝、建沼气池、办铁业社……让老百姓过上好日子，这个朴素的信念，奠定了一个共产党人的初心。', 'http://localhost:9090/files/1733739610089-扶贫助农2.mp4', '2024-12-09 18:21:42', 'http://localhost:9090/files/1733739701447-助农视频2.jpg');
INSERT INTO `video` VALUES (3, '《摆脱贫困》70秒宣传片震撼来袭！', '“中国脱贫历程表明，本着滴水穿石、一张蓝图绘到底的韧性、恒心和奋斗精神，发展中国家的贫困问题是可以解决的，弱鸟是可以先飞、高飞的。中国可以成功，其他发展中国家同样可以成功。”在二十国集团领导人第十九次峰会上，习近平主席发表重要讲话，与世界分享中国成功打赢脱贫攻坚战的经验。', 'http://localhost:9090/files/1733743487943-助农视频3.mp4', '2024-12-09 19:26:45', 'http://localhost:9090/files/1733743601185-助农视频封面3.jpg');
INSERT INTO `video` VALUES (4, '跨越千里，我们与你平分秋色！', '过去一年，抖音电商助力销售农特产总量达42.3亿单！助力农民增收致富！', 'http://localhost:9090/files/1733743844204-助农视频4.mp4', '2024-12-09 19:31:54', 'http://localhost:9090/files/1733743908981-助农视频封面4.jpg');
INSERT INTO `video` VALUES (5, '【中国扶贫人物故事之“新”】直播带货助扶贫', '视频带你深入了解中国扶贫工作中的一个创新篇章——直播带货如何助力扶贫。跟随我们的镜头，您将见证一系列感人至深的故事，这些故事讲述了普通农民如何通过直播平台将他们的农产品直接销售给全国消费者，从而实现脱贫致富。', 'http://localhost:9090/files/1733745618737-xtdowner.com_289002_mp4_360P.mp4', '2024-12-09 20:00:24', 'http://localhost:9090/files/1733745623096-xtdowner.com_289002.jpg');

SET FOREIGN_KEY_CHECKS = 1;
